mapper.select:
SELECT id typeId, brand_id brandId, brand brandName, type typeName FROM tools_mobile_type WHERE TRUE
AND brand_id = #{brandId}
AND id = #{typeId}

SELECT
c.id id, c.mobile mobile, c.owner owner_id, l.name owner_name, c.user user, c.update_time update_time,
t.id typeId, t.type typeName, t.brand_id brandId, t.brand brandName
FROM
tools_mobile_core c join tools_mobile_type t on c.type = t.id join tools_mobile_leader l on c.owner = l.id
WHERE
  TRUE

      AND l.id = #{owner_id}


    AND t.type = #{typeId}


    AND t.brand_id = #{brandId}
